import requestsfrom bs4 import BeautifulSoupimport Agentimport re    def openWeb():    #修改url    url = "http://www.mtime.com/top/movie/top100_chinese/index-10.html"        headers = {'User-Agent':'Mozilla/5.0 (Macintosh; \Intel Mac OS X 10_14_4) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/74.0.3729.157 Safari/537.36'}    r = requests.get(url, headers=headers)    r.encoding = 'utf-8'    if r.status_code == 200:        return BeautifulSoup(r.text,"lxml")    else:        return r.status_code         #ipList = Agent.get_ip()#proxies = Agent.get_random_ip(ipList)soup = openWeb()movData = soup.find_all('div',class_="mov_pic")for num in range(0,10):     #print(movData[num].a["href"])     com_id=re.match(".*/(\d+)",movData[num].a["href"])     print(com_id.group(1))     f=open("时光.csv",'a',newline='')     f.write(com_id.group(1)+'\n')     f.close()                